import { useState, useCallback } from 'react'
import type { SDKRecord, CTable, Fields } from '@looker/visualizations-adapters'
import type { ColumnDef, SortingState, Header } from '@tanstack/table-core'
import {
  getCoreRowModel,
  useReactTable,
  getSortedRowModel,
} from '@tanstack/react-table'
import noop from 'lodash/noop'

type UseHeadlessTableProps = {
  data: SDKRecord[]
  config: CTable
  fields: Fields
}

/**
 * useHeadlessTable handles the configuration of our headless table library based for handling
 * basic table state and sorting logic.
 */

export const useHeadlessTable = ({
  data,
  config,
  fields,
}: UseHeadlessTableProps) => {
  const { column_order } = config

  const flatFields = [...fields.dimensions, ...fields.measures]

  const columns: ColumnDef<SDKRecord>[] = flatFields.map(field => {
    return {
      header: field.label_short,
      accessorFn: data => data[field.name],
      id: field.name,
    }
  })

  const [sorting, setSorting] = useState<SortingState>(
    flatFields
      .map(field => {
        if (field.sorted) {
          return { id: field.name, desc: field.sorted.desc }
        }
        return undefined
      })
      .filter(Boolean) as SortingState
  )

  const handleTableSort = useCallback(
    (header: Header<SDKRecord, unknown>) => {
      if (header?.column?.getCanSort()) {
        const currentDescending = !!sorting.find(
          column => column.id === header.id
        )?.desc
        setSorting([{ id: header.id, desc: !currentDescending }])
      }
    },
    [sorting]
  )

  const table = useReactTable({
    data,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    state: {
      columnOrder: column_order,
      columnPinning: { left: [], right: [] },
      sorting,
    },
    onStateChange: noop,
    renderFallbackValue: null,
  })

  return { table, sorting, handleTableSort }
}
